import React from 'react'
import AuthContext from '../context/ConTexT'
import { useContext,useState } from 'react'
const Addapost = () => {
    let { username ,authTokens} = useContext(AuthContext)
    let [body,usebody] = useState("body")

    

    let addData = async () =>{
        let response = await fetch('http://127.0.0.1:8000/notes/',{
            method:"POST",
            headers: {
                'Content-Type': 'application/json',
                'authorization':'Bearer ' + String(authTokens.access)
            },
            body : JSON.stringify({  message: body,username:username })
        })
        let data =  await response.json()
        console.log(data)
        if (response.status === 200) {
            console.log('saved successfully')
        }
    }


    const bodyonchange = (e) => {
        usebody(e.target.value)
        console.log(body)
    }

    return (
        <div>
        add your post
        <form>
            <input type='text' value={username} />

            <input type='text'  value={body} onChange={bodyonchange} placeholder='enter your tweet' />
            <button onClick={addData}>submit</button>
        </form>



        <div className="card">
        <div className="card-header">
          Quote
        </div>
        <div className="card-body">
          <blockquote className="blockquote mb-0">
            <p><input type='text'  value={body} onChange={bodyonchange} placeholder='enter your tweet' /></p>
            <footer className="blockquote-footer"> by  <cite title="Source Title">{username}</cite></footer>
          </blockquote>
        </div>
      </div>
      <button onClick={addData} className='btn btn-warning'>submit</button>
        
        </div>
  )
}
// username,message
export default Addapost
