import React from 'react'
import AuthContext from '../context/ConTexT'
import { useContext , useState , useEffect } from 'react'
const Home = () => {
  useEffect(() => {
    getnotes()
  }, [])
  const [note, setnote] = useState([])
  let { authTokens } = useContext(AuthContext)
  let getnotes = async () =>{
    const response = await fetch('http://127.0.0.1:8000/notes/',{
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + String(authTokens.access)
      }
    })
    let data=await response.json()
    if (response.status === 200) {
      setnote(data)
    } 
    else {
      alert('Something went  wrong !')
    }
  }


  return (
    <div className='container'>
      <h1>This is home.</h1>
      {note.map((items) => {
        return (
          <>

            <div className='container text-center'style={{"width":"500px"}}>
              <div className="card mb-3">
                <div className="card-body">
                  <h5 className="card-title">{items.username}</h5>
                  <p className="card-text">{items.message}</p>
                  <p className="card-text"><small className="text-muted">Last updated {items.date}</small></p>
                </div>
              </div>
            </div>

          </>

        )


      })}
    </div>
  )
}

export default Home
